
public class TwoDArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int myNumbers [] [] = new int [2] [5];
		myNumbers [0] [0] = 10;
		myNumbers [0] [1] = 20;	
		myNumbers [0] [2] = 30;
		myNumbers [0] [3] = 40;
		myNumbers [0] [4] = 50;
		
		myNumbers [1] [0] = 100;
		myNumbers [1] [1] = 200;
		myNumbers [1] [2] = 300;
		myNumbers [1] [3] = 400;
		myNumbers [1] [4] = 500;
		System.out.println("Total columns: "+ myNumbers.length);
		System.out.println(myNumbers[0].length);
				
	}

}
